var sw={etime:null,erst:null,ego:null,timer:null,now:0,init:()=>{sw.etime=document.getElementById("sw-time"),sw.erst=document.getElementById("sw-rst"),sw.ego=document.getElementById("sw-go"),sw.erst.onclick=sw.reset,sw.ego.onclick=sw.start,sw.erst.disabled=!1,sw.ego.disabled=!1},start:()=>{sw.timer=setInterval(sw.tick,10),sw.ego.value="Stop",sw.ego.onclick=sw.stop},stop:()=>{clearInterval(sw.timer),sw.timer=null,sw.ego.value="Start",sw.ego.onclick=sw.start},tick:()=>{sw.now++;let e=0,t=0,s=0;remain=sw.now,e=Math.floor(remain/3600),remain-=3600*e,t=Math.floor(remain/60),remain-=60*t,s=remain,e<10&&(e="0"+e),t<10&&(t="0"+t),s<10&&(s="0"+s),sw.etime.innerHTML=e+":"+t+":"+s},reset:()=>{null!=sw.timer&&sw.stop(),sw.now=-1,sw.tick()}};window.addEventListener("load",sw.init);