const result=document.getElementById("output");function checkPalindrome(){var e=document.getElementById("output"),t=document.getElementById("input").value,n=t.toLowerCase().replace(/[^A-Z0-9]/gi,"");reverseInput=n.split("").reverse().join(""),n===reverseInput?(e.innerHTML=t+" is a palindrome.",document.getElementById("input").value="",setTimeout((()=>{e.innerHTML=""}),3e3)):(e.innerHTML=t+" is not a palindrome.",document.getElementById("input").value="",setTimeout((()=>{e.innerHTML=""}),3e3))}