function randomNumber(){let e=Math.floor(10*Math.random()),t=Math.floor(10*Math.random());document.getElementById("primaryNumber").innerHTML=e,document.getElementById("secondaryNumber").innerHTML=t}function checkResult(){let num1=document.getElementById("primaryNumber").innerHTML,num2=document.getElementById("secondaryNumber").innerHTML,result=document.getElementById("guess").value,answer=eval(num1+"+"+num2);answer==result?(document.getElementById("guess").style.backgroundColor="green",setTimeout((()=>{document.getElementById("guess").value="",document.getElementById("guess").style.backgroundColor="white",randomNumber()}),1e3)):(document.getElementById("guess").style.backgroundColor="red",setTimeout((()=>{document.getElementById("guess").value="",document.getElementById("guess").style.backgroundColor="white",randomNumber()}),1e3))}